// HomePage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [userId, setUserId] = useState('');

  const handleSearch = () => {
    if (userId.trim() !== '') {
      // Use the Link component for navigation
      // The `to` attribute specifies the target URL
      return <Link to={`/user-tickets/${userId}`} />;
    } else {
      alert('Please enter a valid User ID.');
    }
  };

  return (
    <div>
      <h1>Home Page</h1>
      <label>
        Search by User ID:
        <input type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
      </label>
      {/* Call the handleSearch function when the button is clicked */}
      <button onClick={handleSearch}>Search by User ID</button>
      {/* Use Link directly for navigation */}
      <Link to="/view-ticket">
        <button>View All Tickets</button>
      </Link>
    </div>
  );
};

export default HomePage;
