  return (
    <div>
      <h1>Ticket List</h1>
      {tickets.length === 0 ? (
        <p>No tickets available.</p>
      ) : (
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket._id}>
              <p>User: {ticket.user}</p>
              <p>Category: {ticket.Category}</p>
              <p>Sub Category: {ticket.Sub_Category}</p>
              <p>Status: {ticket.Status}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TicketList;
