// Import necessary modules
import React, { useState } from 'react';
import './loginComponent.css'; // Import your CSS file for specific styling
import Cookies from 'js-cookie';

const LoginForm = ({ handleLogin }) => {
  const [formData, setFormData] = useState({
    Email: '',
    password: '',
  });

  const [loginMessage, setLoginMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/api/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
        credentials: 'include',
      });

      if (response.ok) {
        const responseData = await response.json();

        // Assuming your authentication token is named 'auth_token'
        const authToken = responseData.auth_token;

        // Assuming the user ID is available in responseData.user.id
        const userId = responseData.user.id;

        // Store the auth token and user ID in cookies
        Cookies.set('auth_token', authToken, { expires: 7 }); // Expires in 7 days
        Cookies.set('user_id', userId, { expires: 7 }); // Expires in 7 days

        const role = responseData.user.role;
        localStorage.setItem('loginMessage', JSON.stringify(responseData));
        setLoginMessage(`Login successful. User ID: ${userId}, User role: ${role}`);
        handleLogin(role);
      } else {
        setLoginMessage('Login failed. Please check your email and password.');
      }
    } catch (error) {
      console.error('Error:', error);
      setLoginMessage('An error occurred during login.');
    }
  };

  return (
    <div className="login-form-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <label>
          Email:
          <input
            type="email"
            name="Email"
            value={formData.Email}
            onChange={handleChange}
          />
        </label>

        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </label>

        <button type="submit">Login</button>
      </form>

      {loginMessage && <p className="login-message">{loginMessage}</p>}
    </div>
  );
};

export default LoginForm;
