import '../App.css';
import React, { useState } from 'react';
import Cookies from 'js-cookie';


const TicketForm = () => {
  const [ticketData, setTicketData] = useState({
    Category: '',
    Sub_Category: '',
    TDescribtion: '',
  });

  const [ticketMessage, setTicketMessage] = useState('');

  // useEffect(() => {
  //   // You can use this effect to load the user ID from the cookie
  //   const userId = Cookies.get('user_id');
  //   // Do something with the user ID if needed
  // }, []); // The empty dependency array ensures this effect runs once on component mount

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTicketData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleTicketCreation = async () => {
    try {
      // Get user ID from the cookie
      const userId = Cookies.get('user_id');

      const response = await fetch('http://localhost:3000/Tickets/createTicket', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          ...ticketData,
          user: userId, // Pass the user ID to the server
        }),
      });

      if (response.ok) {
        setTicketMessage('Ticket created successfully');
      } else {
        setTicketMessage('Choose a Category/sub Category');
      }
    } catch (error) {
      console.error('Error:', error);
      setTicketMessage('An error occurred during the process of creating a ticket. Please try again.');
    }
  };

  const categoryOptions = ['Hardware', 'Software', 'Network'];
  const Sub_CategoryOptions = {
    Hardware: ['Desktops', 'Laptops', 'Printers', 'Servers', 'Networking equipment'],
    Software: ['Operating system', 'Application software', 'Custom software', 'Integration issues'],
    Network: ['Email issues', 'Internet connection problems', 'Website errors'],
  };

  return (
    <div>
      <h2>Create a New Ticket</h2>
      <form onSubmit={handleTicketCreation}>
        <label>
          Category:
          <select
            name="Category"
            value={ticketData.Category}
            onChange={handleChange}
            required
          >
            <option value="" disabled>Select a Category</option>
            {categoryOptions.map((Category) => (
              <option key={Category} value={Category}>
                {Category}
              </option>
            ))}
          </select>
        </label>

        <br />

        <label>
          Sub Category:
          <select
            name="Sub_Category"
            value={ticketData.Sub_Category}
            onChange={handleChange}
            required
          >
            <option value="" disabled>Select a Sub Category</option>
            {ticketData.Category &&
              Sub_CategoryOptions[ticketData.Category].map((Sub_Category) => (
                <option key={Sub_Category} value={Sub_Category}>
                  {Sub_Category}
                </option>
              ))}
          </select>
        </label>

        <br />

        <label>
          Description:
          <textarea
            name="TDescribtion"
            value={ticketData.TDescribtion}
            onChange={handleChange}
            required
          />
        </label>

        <br />

        <button type="submit">Create Ticket</button>
      </form>
      {ticketMessage && <p>{ticketMessage}</p>}
    </div>
  );
};

export default TicketForm;
