// TicketByUserId.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const TicketByUserId = () => {
  const { _id } = useParams();
  const [tickets, setTickets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch(`http://localhost:3000/Tickets/getTicketsByUserId/${_id}`, {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    fetchTickets();
  }, [_id]);

  return (
    <div>
      <h1>Tickets by User ID: {_id}</h1>
      {tickets.length > 0 ? (
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket._id}>
              <p>User ID: {ticket.user}</p>
              <p>Category: {ticket.Category}</p>
              <p>Sub Category: {ticket.Sub_Category}</p>
              <p>Status: {ticket.Status}</p>
              <p>Description: {ticket.TDescribtion}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tickets found for this user.</p>
      )}
      <button onClick={() => navigate('/view-ticket')}>Back to All Tickets</button>
    </div>
  );
};

export default TicketByUserId;
