import React, { useState, useEffect } from 'react';

const UpdateWorkflow = ({ match }) => {
  const [formData, setFormData] = useState({
    user: '',
    Category: '',
    Sub_Category: '',
    Workflow_Steps: '',
    Issue_Type: '',
  });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    // Fetch existing workflow details and populate the form
    const fetchWorkflowById = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/v1/Workflow/getWorkflowById/${match.params.id}`);
        if (response.ok) {
          const data = await response.json();
          setFormData({
            user: data.user,
            Category: data.Category,
            Sub_Category: data.Sub_Category,
            Workflow_Steps: data.Workflow_Steps,
            Issue_Type: data.Issue_Type,
          });
          setError('');
        } else if (response.status === 404) {
          setError('Workflow not found');
        } else {
          setError('Error fetching workflow');
        }
      } catch (error) {
        console.error('Error fetching workflow:', error.message);
        setError('An unexpected error occurred');
      }
    };

    fetchWorkflowById();
  }, [match.params.id]);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:3000/api/v1/Workflow/updateWorkflowById/${match.params.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const updatedWorkflow = await response.json();
        setSuccessMessage('Workflow updated successfully');
        setError('');
        console.log('Updated Workflow:', updatedWorkflow);
      } else {
        const errorMessage = await response.json();
        setError(errorMessage.error || 'Error updating workflow');
        setSuccessMessage('');
      }
    } catch (error) {
      console.error('Error updating workflow:', error.message);
      setError('An unexpected error occurred');
      setSuccessMessage('');
    }
  };

  return (
    <div>
      <h2>Update Workflow</h2>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

      <form onSubmit={handleFormSubmit}>
        <div>
          <label>User:</label>
          <input type="text" name="user" value={formData.user} onChange={handleInputChange} />
        </div>
        <div>
          <label>Category:</label>
          <input type="text" name="Category" value={formData.Category} onChange={handleInputChange} />
        </div>
        <div>
          <label>Sub-Category:</label>
          <input type="text" name="Sub_Category" value={formData.Sub_Category} onChange={handleInputChange} />
        </div>
        <div>
          <label>Workflow Steps:</label>
          <input type="text" name="Workflow_Steps" value={formData.Workflow_Steps} onChange={handleInputChange} />
        </div>
        <div>
          <label>Issue Type:</label>
          <input type="text" name="Issue_Type" value={formData.Issue_Type} onChange={handleInputChange} />
        </div>
        <button type="submit">Update Workflow</button>
      </form>
    </div>
  );
};

export default UpdateWorkflow;
