// TicketByAgentId.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TicketByAgentId = () => {
  const [agentId, setAgentId] = useState('');
  const [reports, setReports] = useState([]);
  const navigate = useNavigate();

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:3000/Reports/getReportsByAgentId/${agentId}`, {
        headers: {
          'Accept': 'application/json',
        },
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setReports(data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  return (
    <div>
      <h1>Reports by Agent ID</h1>
      <label>
        Enter Agent ID:
        <input type="text" value={agentId} onChange={(e) => setAgentId(e.target.value)} />
      </label>
      <button onClick={handleSearch}>Search</button>

      {reports.length > 0 ? (
        <ul>
          {reports.map((report) => (
            <li key={report._id}>
              <p>Agent ID: {report.agent}</p>
              {/* Include other relevant report details */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports found for this agent.</p>
      )}
      <button onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
    </div>
  );
};

export default TicketByAgentId;
