import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import LoginForm from './components/loginComponent'; // Update the path
import RegisterForm from './components/registerComponent';
import HomePage from './components/homeComponent';
import AdminDashboard from './components/Admin';
import UserDashboard from './components/User';
import ManagerDashboard from './components/Manager';
import AgentDashboard from './components/Agent';
import CreateTicket from './components/createTicketComponent';

import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

const App = () => {
  const [userRole, setUserRole] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const storedRole = localStorage.getItem('userRole');
    const storedUserId = localStorage.getItem('userId');

    if (storedRole) {
      setUserRole(storedRole);
    }

    if (storedUserId) {
      setUserId(storedUserId);
    }
  }, []);

  const handleLogin = (role, id) => {
    setUserRole(role);
    setUserId(id);
    localStorage.setItem('userRole', role);
    localStorage.setItem('userId', id);
    window.location.replace(`/${role.toLowerCase()}`);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={<HomePage userRole={userRole} setUserRole={setUserRole} />}
          />
          <Route path="/login" element={<LoginForm handleLogin={handleLogin} />} />
          <Route path="/register" element={<RegisterForm />} />

          {/* Actual dashboard components */}
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="/user" element={<UserDashboard />} />
          <Route path="/manager" element={<ManagerDashboard />} />
          <Route path="/agent" element={<AgentDashboard />} />
          <Route path="/create-ticket" element={<CreateTicket userId={userId} />} />

          {/* Redirect to login if the user is not authenticated */}
          <Route path="" element={<Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));

export default App;
