import React from 'react';
import { Link, useHistory } from 'react-router-dom'; // Import Link and useHistory
import Cookies from 'js-cookie';

const User = () => {
  const history = useHistory(); // Use the useHistory hook to access the history object

  const handleCreateTicketClick = () => {
    // Get user ID from the cookie
    const userId = Cookies.get('user_id');

    // Use history.push to navigate to the CreateTicketComponent
    history.push({
      pathname: '/create-ticket',
      state: { userId: userId }, // Pass user ID as state
    });
  };

  return (
    <div>
      <h1>Hello User</h1>

      {/* Use a function to handle the button click */}
      <button onClick={handleCreateTicketClick}>Create Ticket</button>
    </div>
  );
};

export default User;
