import '../App.css';
import React, { useState } from 'react';

const LoginForm = () => {
  console.log('LoginForm rendering...');

  const [formData, setFormData] = useState({
    Email: '',
    password: '',
  });

  const [loginMessage, setLoginMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/api/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
        credentials: 'include', 
      });

      if (response.ok) {
        const { token } = await response.json();
        localStorage.setItem('authToken', token);
        setLoginMessage('Login successful');
        // Fetch data after successful login
        fetchData();
      } else {
        setLoginMessage('Login failed. Please check your email and password.');
      }
    } catch (error) {
      console.error('Error:', error);
      console.error('Error Stack Trace:', error.stack);
      setLoginMessage(error.message || 'An error occurred during login.');
    }
  };

  const fetchData = async () => {
    const authToken = localStorage.getItem('authToken');
    if (authToken) {
      try {
        const response = await fetch('http://localhost:3000/api/v1/protected-endpoint', {
          headers: {
            'Authorization': `Bearer ${authToken}`,
          },
          credentials: 'include',
        });

        if (response.ok) {
          const data = await response.json();
          // Process the data
        } else {
          // Handle request failure (e.g., token expired or invalid)
        }
      } catch (error) {
        console.error('Error:', error);
      }
    } else {
      // User is not authenticated, handle accordingly
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            name="Email"
            value={formData.Email}
            onChange={handleChange}
          />
        </label>

        <br />

        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </label>

        <button type="submit">Login</button>
      </form>

      {loginMessage && <p>{loginMessage}</p>}
    </div>
  );
};

export default LoginForm;
