import './TicketList.css';
import React, { useEffect, useState } from 'react';

const TicketsPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const fetchAllTickets = async () => {
      try {
        const response = await fetch('http://localhost:3000/Tickets/getAllTickets', {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setTickets(data);
        setSearchResults(data); // Initialize searchResults with all tickets
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tickets:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAllTickets();
  }, []);

  const handleSearch = async () => {
    try {
      let url = 'http://localhost:3000/Tickets/getAllTickets';
      if (userId.trim() !== '') {
        url = `http://localhost:3000/Tickets/getTicketsByUserId/${userId}`;
      }

      const response = await fetch(url, {
        headers: {
          'Accept': 'application/json',
        },
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error searching tickets:', error);
      setError(error.message);
    }
  };

  const handleViewAllTickets = async () => {
    setSearchTerm('');
    setUserId('');
    fetchAllTickets();
  };

  const fetchAllTickets = async () => {
    try {
      const response = await fetch('http://localhost:3000/Tickets/getAllTickets', {
        headers: {
          'Accept': 'application/json',
        },
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setTickets(data);
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
      setError(error.message);
    }
  };

  return (
    <div className="ticket-page-container">
      {/* Search bar and buttons */}
      <div className="search-bar">
        <label>
          Search by User ID:
          <input type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
        </label>
        <button onClick={handleSearch}>Search by User ID</button>
        <button onClick={handleViewAllTickets}>View All Tickets</button>
      </div>

      {/* Display all tickets below the search bar */}
      {loading && <p className="loading-message">Loading...</p>}
      {error && <p className="error-message">Error: {error}</p>}
      {searchResults.length > 0 ? (
        <ul>
          {searchResults.map((ticket) => (
            <li key={ticket._id}>
              <p>User ID: {ticket.user}</p>
              <p>Category: {ticket.Category}</p>
              <p>Sub Category: {ticket.Sub_Category}</p>
              <p>Status: {ticket.Status}</p>
              <p>Description: {ticket.TDescribtion}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No matching tickets found.</p>
      )}
    </div>
  );
};

export default TicketsPage;
