import React, { useEffect, useState } from 'react';

const FAQComponent = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [FAQ, setFAQ] = useState([]);
  const [Category, setCategory] = useState('');
  const [Sub_Category, setSubCategory] = useState('');

  const handleSearch = async () => {
    try {
      const response = await fetch('http://localhost:3000/FAQ/searchFAQ', {
        method: 'POST', // Change the method to POST
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json', // Specify the content type
        },
        credentials: 'include',
        body: JSON.stringify({ Category, Sub_Category }), // Send search parameters in the request body
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setFAQ(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching FAQs:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>All FAQs Page</h1>
      <div>
        <label htmlFor="category">Category:</label>
        <input
          type="text"
          value={Category}
          onChange={(e) => setCategory(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="subCategory">Sub-Category:</label>
        <input
          type="text"
          value={Sub_Category}
          onChange={(e) => setSubCategory(e.target.value)}
        />
      </div>
      <button onClick={handleSearch}>Search FAQs</button>

      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {FAQ.length > 0 ? (
        <ul>
          {FAQ.map((faq) => (
            <li key={faq._id}>
              <p>User ID: {faq.user}</p>
              <p>Category: {faq.Category}</p>
              <p>Sub Category: {faq.Sub_Category}</p>
              <p>Description: {faq.TDescribtion}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No FAQs found.</p>
      )}
    </div>
  );
};

export default FAQComponent;
