import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import Link and useNavigate
import Cookies from 'js-cookie';

const User = () => {
  const navigate = useNavigate(); // Use the useNavigate hook to get the navigation function

  const handleCreateTicketClick = () => {
    // Get user ID from the cookie
    const userId = Cookies.get('user_id');

    // Use navigate function to navigate to the CreateTicketComponent
    navigate('/create-ticket', {
      state: { userId: userId }, // Pass user ID as state
    });
  };

  return (
    <div>
      <h1>Hello User</h1>

      {/* Use a function to handle the button click */}
      <button onClick={handleCreateTicketClick}>Create Ticket</button>
    </div>
  );
};

export default User;
