import React, { useEffect, useState } from 'react';

const FAQComponent = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [FAQ, setFAQ] = useState([]);
  const [Category, setCategory] = useState('');
  const [Sub_Category, setSubCategory] = useState('');
  const [categoriesOptions, setCategoriesOptions] = useState([
    'Hardware', 'Software', 'Network'
    // Add more categories as needed
  ]);
  const [subCategoriesOptions, setSubCategoriesOptions] = useState([
    'Desktops', 'Laptops', 'Printers', 'Servers', 'Networking equipment',
    'Operating system', 'Application software', 'Custom software', 'Integration issues',
    'Email issues', 'Internet connection problems', 'Website errors'
    // Add more sub-categories as needed
  ]);

  useEffect(() => {
    setLoading(false); // No need to fetch options, they are hardcoded
  }, []);

  const handleSearch = async () => {
    try {
      const response = await fetch('http://localhost:3000/FAQ/searchFAQ', {
        method: 'GET', // Use GET method for searching
        headers: {
          'Accept': 'application/json',
        },
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setFAQ(data);
    } catch (error) {
      console.error('Error fetching FAQs:', error);
      setError(error.message);
    }
  };

  return (
    <div>
      <h1>All FAQs Page</h1>
      <div>
        <label htmlFor="category">Category:</label>
        <select
          value={Category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categoriesOptions.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="subCategory">Sub-Category:</label>
        <select
          value={Sub_Category}
          onChange={(e) => setSubCategory(e.target.value)}
        >
          <option value="">Select Sub-Category</option>
          {subCategoriesOptions.map((subCategory) => (
            <option key={subCategory} value={subCategory}>
              {subCategory}
            </option>
          ))}
        </select>
      </div>
      <button onClick={handleSearch}>Search FAQs</button>

      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {FAQ.length > 0 ? (
        <ul>
          {FAQ.map((faq) => (
            <li key={faq._id}>
              <p>User ID: {faq.user}</p>
              <p>Category: {faq.Category}</p>
              <p>Sub Category: {faq.Sub_Category}</p>
              <p>Description: {faq.TDescribtion}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No FAQs found.</p>
      )}
    </div>
  );
};

export default FAQComponent;
