import React, { useEffect, useState } from 'react';

const FAQSearchPage = () => {
  const [Category, setCategory] = useState('');
  const [Sub_Category, setSubCategory] = useState('');
  const [FAQ, setFAQ] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const subCategoriesOptions = [
    'Desktops', 'Laptops', 'Printers', 'Servers', 'Networking equipment',
    'Operating system', 'Application software', 'Custom software', 'Integration issues',
    'Email issues', 'Internet connection problems', 'Website errors'
  ];

  useEffect(() => {
    const fetchAllTickets = async () => {
      try {
        const response = await fetch('http://localhost:3000/Tickets/getAllTickets', {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setFAQ(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tickets:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAllTickets();
  }, []);

  return (
    <div>
      <h2>FAQ Search</h2>
      <div>
        <label htmlFor="Category">Category:</label>
        <select>
          value={Category}
          <option value="">Select Category</option>
          <option value="Hardware">Hardware</option>
          <option value="Software">Software</option>
          <option value="Network">Network</option>
          onChange={(e) => setCategory(e.target.value)}
        </select>
      </div>
      <div>
        <label htmlFor="subCategory">Sub-Category:</label>
        <select
          id="Sub_Category"
          value={Sub_Category}
          onChange={(e) => setSubCategory(e.target.value)}
        >
          <option value="">Select Sub-Category</option>
          {subCategoriesOptions.map((subCat) => (
            <option key={subCat} value={subCat}>
              {subCat}
            </option>
          ))}
