import './TicketList.css';
import React, { useEffect, useState } from 'react';

const TicketsPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:3000/Tickets/getAllTickets', {
          headers: {
            'Accept': 'application/json',
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setTickets(data);
        setSearchResults(data); // Initialize searchResults with all tickets
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tickets:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchTickets();
  }, []);

  const handleSearch = () => {
    const filteredTickets = tickets.filter((ticket) => {
      const { user, Category, Sub_Category, Status, TDescribtion } = ticket;
      const searchTermLower = searchTerm.toLowerCase();
      return (
        user.toLowerCase().includes(searchTermLower) ||
        Category.toLowerCase().includes(searchTermLower) ||
        Sub_Category.toLowerCase().includes(searchTermLower) ||
        Status.toLowerCase().includes(searchTermLower) ||
        TDescribtion.toLowerCase().includes(searchTermLower)
      );
    });

    setSearchResults(filteredTickets);
  };

  return (
    <div className="ticket-page-container">
      <h1>All Tickets</h1>
      <form onSubmit={(e) => { e.preventDefault(); handleSearch(); }}>
        <label>
          Search:
          <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
        </label>
        <button type="submit">Search</button>
      </form>
      {/* {loading && <p className="loading-message">Loading...</p>} */}
      {error && <p className="error-message">Error: {error}</p>}
      {!loading && !error && (
        <ul>
          {searchResults.map((ticket) => (
            <li key={ticket._id}>
              <p>User ID: {ticket.user}</p>
              <p>Category: {ticket.Category}</p>
              <p>Sub Category: {ticket.Sub_Category}</p>
              <p>Status: {ticket.Status}</p>
              <p>Description: {ticket.TDescribtion}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TicketsPage;
