import React, { useState, useEffect } from 'react';

const TicketPage = () => {
  const [tickets, setTickets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('id');

  useEffect(() => {
    // Fetch all tickets from the database
    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:3000/Tickets/getAllTickets');
        const data = await response.json();
        setTickets(data);
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    fetchTickets();
  }, []);

  const handleSearch = async () => {
    try {
      const response = await fetch(`/api/tickets/search?${searchType}=${searchTerm}`);
      const data = await response.json();
      setTickets(data);
    } catch (error) {
      console.error('Error searching tickets:', error);
    }
  };

  return (
    <div>
      <h1>Tickets</h1>

      {/* Search bar */}
      <div>
        <label>
          Search by:
          <select onChange={(e) => setSearchType(e.target.value)}>
            <option value="id">ID</option>
            <option value="Category">Category</option>
            <option value="Sub_Category">Sub Category</option>
            <option value="Status">Status</option>
            <option value="TDescribtion">Description</option>
          </select>
        </label>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Display tickets */}
      <div>
        {tickets.map((ticket) => (
          <div key={ticket._id}>
            <p>ID: {ticket._id}</p>
            <p>Category: {ticket.Category}</p>
            <p>Sub Category: {ticket.Sub_Category}</p>
            <p>Status: {ticket.Status}</p>
            <p>Description: {ticket.TDescribtion}</p>
            {/* Add more ticket details as needed */}
            <hr />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TicketPage;
