// TicketsPage.js
import '../App.css';
import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';

const TicketsPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const fetchTickets = async () => {
      const storedUserId = Cookies.get('user_id');
      console.log('Stored User ID:', storedUserId);
      setUserId(storedUserId);
      try {
        // Fetch authentication token from your storage (e.g., localStorage, cookies)

        const response = await fetch('http://localhost:3000/Tickets/getAllTickets', {
          headers: {
            'Accept': 'application/json',
            'Authorization': `Bearer ${authToken}`, // Include the token in the Authorization header
          },
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setTickets(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tickets:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchTickets();
  }, []);

  return (
    <div>
      <h1>All Tickets</h1>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && (
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket._id}>
              <p>User ID: {ticket.user}</p>
              <p>Category: {ticket.Category}</p>
              <p>Sub Category: {ticket.Sub_Category}</p>
              <p>Status: {ticket.Status}</p>
              <p>Description: {ticket.TDescribtion}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TicketsPage;
